import { createRef } from 'react'
import React from 'react'

const AuthenticatedLink= ({ url, filename, children })=> {
  const link = createRef()

  const handleAction = async () => {
    if (link.current.href) { return }

    const result = await fetch(url, {	
      headers: {"Content-type":"image/png","Content-Disposition":"attachment","filename":"picture.png"}
    })

    const blob = await result.blob()
    const href = window.URL.createObjectURL(blob)

    link.current.download = filename
    link.current.href = href

    link.current.click()
  }

  return (
      <div>
      <a role='button' ref={link} onClick={handleAction}>{children}</a>
      </div>

  )
}

export default AuthenticatedLink;
